import React, { useState } from "react";
import {
  StyleSheet,
  Image,
  View,
  ImageBackground,
  TextInput,
  Dimensions,
  TouchableOpacity,
  Text,
} from "react-native";

import bgImage from "./images/background.jpg";
import logo from "./images/Logo.png";
import Icon from "react-native-vector-icons/MaterialIcons";

const { width: WIDTH } = Dimensions.get("window");

export default function App() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [toggled, setToggled] = useState(false);

  function sendCredentials() {
    fetch("http//192.168.1.2:3000/api/user/login")
      .then((response) => response.body)
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }

  return (
    <ImageBackground source={bgImage} style={styles.backgroundContainer}>
      <View style={styles.logoContainer}>
        <Image source={logo} style={styles.logo} />
      </View>

      <View style={styles.inputContainer}>
        <Icon
          name={"person-outline"}
          size={28}
          color={"rgba(255,255,255, 0.7)"}
          style={styles.inputIcon}
        />
        <TextInput
          style={styles.input}
          placeholder={"Email"}
          placeholderTextColor={"rgba(255,255,255,0.7)"}
          underlineColorAndroid="transparent"
          value={email}
          onChangeText={(input) => setEmail(input)}
          autoCapitalize="none"
          autoCorrect={false}
        />
      </View>

      <View style={styles.inputContainer}>
        <Icon
          name={"lock"}
          size={28}
          color={"rgba(255,255,255, 0.7)"}
          style={styles.inputIcon}
        />
        <TextInput
          style={styles.input}
          placeholder={"Password"}
          secureTextEntry={toggled}
          placeholderTextColor={"rgba(255,255,255,0.7)"}
          underlineColorAndroid="transparent"
          value={password}
          onChangeText={(input) => setPassword(input)}
          autoCapitalize="none"
          autoCorrect={false}
        />

        <TouchableOpacity
          style={styles.btnEye}
          onPress={() => setToggled((toggled) => !toggled)}
        >
          <Icon
            name={toggled ? "visibility-off" : "visibility"}
            size={26}
            color={"rgba(255,255,255,0.7)"}
          />
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.btnLogin}
          onPress={() => sendCredentials()}
        >
          <Text style={styles.text}>Login</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  backgroundContainer: {
    flex: 1,
    width: null,
    height: null,
    justifyContent: "center",
    alignItems: "center",
  },
  logoContainer: {
    alignItems: "center",
    marginBottom: 80,
    marginTop: 60,
  },
  logo: {
    width: 180,
    height: 95,
  },
  input: {
    width: WIDTH - 55,
    height: 40,
    borderRadius: 45,
    fontSize: 16,
    paddingLeft: 45,
    backgroundColor: "rgba(0,0,0,0.35)",
    color: "rgba(255,255,255,0.7)",
    marginHorizontal: 25,
  },
  inputIcon: {
    position: "absolute",
    top: 7,
    left: 35,
  },
  inputContainer: {
    marginTop: 10,
  },
  btnEye: {
    position: "absolute",
    top: 7,
    right: 46,
  },
  btnLogin: {
    width: WIDTH - 55,
    height: 45,
    borderRadius: 45,
    backgroundColor: "#432577",
    justifyContent: "center",
    marginTop: 20,
    marginLeft: 25,
  },
  text: {
    color: "rgba(255,255,255,0.7)",
    fontSize: 16,
    textAlign: "center",
  },
});
